import axios from "axios";
import { REGISTER_API, LOGIN_API, PROFILE_API } from "./config";

axios.interceptors.response.use(
  (res) => res,
  (err) => {
    if (err.response) {
      if (err.response.status === 401) {
        localStorage.clear();
        window.location.href = "login";
      }
      return Promise.reject(err);
    }
  }
);

const getToken = () => {
  try {
    const USER_SESSION = JSON.parse(localStorage?.session);
    if (isExpired(USER_SESSION?.access_token)) return false;
    return true;
  } catch (error) {
    if (error) return false;
  }
};

// axios.defaults.headers.common["x-auth-token"] =

export const REGISTER_SERVICE = (data) =>
  axios({
    method: "POST",
    url: REGISTER_API,
    headers: {
      "content-type": "application/json",
    },
    data: data,
  });

export const LOGIN_SERVICE = (data) =>
  axios({
    method: "POST",
    url: LOGIN_API,
    headers: {
      "content-type": "application/json",
    },
    data: data,
  });

export const PROFILE_SERVICE = (data) =>
  axios({
    method: "POST",
    url: PROFILE_API,
    headers: {
      "content-type": "application/json",
    },
    data: data,
  });
