import React, { useEffect, useState } from "react";
import { GET_POSTS_SERVICE } from "../api/service";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { Button } from "react-bootstrap";
import { POST_LIKE_SERVICE, POST_UNLIKE_SERVICE } from "../api/service";

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const data = useSelector((state) => state.authReducer?.user);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const res = await GET_POSTS_SERVICE();
        setPosts(res.data);
        console.log(res.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchPosts();
  }, []);

  const handleLike = async (id) => {
    //store init likes
    const post = posts.find((post) => post._id === id);
    console.log(post);
    //set new like in state
    setPosts([...posts, { user: data?._id, _id: id }]);
    //api call

    try {
      const res = await POST_LIKE_SERVICE(id);
      console.log(res);
    } catch (error) {
      console.log(error);
    }
    console.log(id);
  };

  const handleDisLike = (id) => {
    console.log(id);
  };

  return (
    <>
      <h1>posts</h1>
      <Link className="btn btn-primary" to="/add-post">
        Add Post
      </Link>
      {posts.map((post) => {
        return (
          <>
            <div className="bg-light" key={post._id}>
              <p>{post.content}</p>
              <p>Total Likes: {post.likes.length}</p>
              <Button onClick={() => handleLike(post._id)}>Like</Button>
              <Button onClick={() => handleDisLike(post._id)}>DisLike</Button>
              <p>{post.user === data?._id ? "Can delete" : "Cannot delete"}</p>
            </div>
          </>
        );
      })}
    </>
  );
};

export default Posts;
