import React, { useEffect, useState } from "react";
import { GET_POSTS_SERVICE } from "../api/service";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const data = useSelector((state) => state.authReducer?.user);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const res = await GET_POSTS_SERVICE();
        setPosts(res.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchPosts();
  }, []);
  return (
    <>
      <h1>posts</h1>
      <Link className="btn btn-primary">Add Post</Link>
      {posts.map((post) => {
        return (
          <>
            <p>{post.content}</p>
            <p>{post.user === data?._id ? "Can delete" : "Cannot delete"}</p>
          </>
        );
      })}
    </>
  );
};

export default Posts;
