import axios from "axios";
import { REGISTER_API, LOGIN_API, PROFILE_API } from "./config";

axios.interceptors.response.use(
  (res) => res,
  (err) => {
    if (err.response) {
      if (err.response.status === 401) {
        localStorage.clear();
        window.location = "/login";
      }
      return Promise.reject(err);
    }
  }
);

export const REGISTER_SERVICE = (data) =>
  axios({
    method: "POST",
    url: REGISTER_API,
    headers: {
      "content-type": "application/json",
    },
    data: data,
  });

export const LOGIN_SERVICE = (data) =>
  axios({
    method: "POST",
    url: LOGIN_API,
    headers: {
      "content-type": "application/json",
    },
    data: data,
  });

export const PROFILE_SERVICE = (data) =>
  axios({
    method: "POST",
    url: PROFILE_API,
    headers: {
      "content-type": "application/json",
      "x-auth-token": JSON.parse(localStorage?.session).access_token,
    },
    data: data,
  });
