import axios from "axios";
import { REGISTER_API, LOGIN_API, PROFILE_API } from "./config";
import { getAuthToken } from "../auth/auth.token";

axios.defaults.headers.common["x-auth-token"] = getAuthToken();

axios.interceptors.response.use(
  (res) => res,
  (err) => {
    if (err.response) {
      if (err.response.status === 401) {
        console.log(err);
        localStorage.clear();
        window.location.href = "login";
      }
      return Promise.reject(err);
    }
  }
);

export const REGISTER_SERVICE = (data) =>
  axios({
    method: "POST",
    url: REGISTER_API,
    headers: {
      "content-type": "application/json",
    },
    data: data,
  });

export const LOGIN_SERVICE = (data) =>
  axios({
    method: "POST",
    url: LOGIN_API,
    headers: {
      "content-type": "application/json",
    },
    data: data,
  });

export const PROFILE_SERVICE = (data) =>
  axios({
    method: "POST",
    url: PROFILE_API,
    headers: {
      "content-type": "application/json",
      "x-auth-token": getAuthToken(),
    },
    data: data,
  });
